import React from 'react';
import "../tailwind.output.css"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faEye} from "@fortawesome/free-solid-svg-icons";

const Regex = RegExp(/^\s?[A-Z0–9]+[A-Z0–9._+-]{0,}@[A-Z0–9._+-]+\.[A-Z0–9]{2,4}\s?$/i);

interface SignUpProps {
    name?: any;
    value?: any;
}

interface SignUpState {
    userName : string,
    fullName : string,
    eMail : string,
    passWord : string,
    errors : {
        userName :  string,
        fullName : string,
        eMail : string,
        passWord : string
    }
}

export class SignUpForm extends React.Component<SignUpProps, SignUpState> {

    constructor(props: SignUpProps) {
        super(props);
        this.state = {
            userName: '',
            fullName: '',
            eMail: '',
            passWord: '',
            errors: {
                userName: '',
                fullName: '',
                eMail: '',
                passWord: ''
            }
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = (event : any) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;
        switch (name) {
            case 'userName':
                errors.userName = value.length < 5 ? 'Must be 5 characters long!': '';
                break;
            case 'fullName':
                errors.fullName = value.length < 5 ? 'Must be 5 characters long!': '';
                break;
            case 'eMail':
                errors.eMail = Regex.test(value)? '': 'Email is not valid!';
                break;
            case 'passWord':
                errors.passWord = value.length < 8 ? 'Must be 8 characters long!': '';
                break;
            default:
                break;
        }
        this.setState(Object.assign(this.state, { errors,[name]: value }));
        console.log(this.state.errors);
    }

    handleSubmit = (event : any) => {
        event.preventDefault();
        let validity = true;
        Object.values(this.state.errors).forEach(
            (val) => val.length > 0 && (validity = false)
        );
        if(validity){
            console.log("Registering can be done");
        }else{
            console.log("You cannot be registered!!!")
        }
    }

    render () {
        const {errors} = this.state
        return (
            <form className="w-full max-w-lg" onSubmit={this.handleSubmit} noValidate>
                <div className="flex flex-wrap -mx-3 mb-6">
                    <div className="userName w-full md:w-1/2 px-3 mb-6 md:mb-0">
                        <label htmlFor="userName"
                               className="block uppercase tracking-wide text-gray-700 text-sm font-medium mb-2">
                            Username
                        </label>
                        <input
                            className="appearance-none block w-full text-gray-700 border border-gray-400
                            rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                            type="text" placeholder="Username" name='userName'
                            onChange={this.handleChange}/>
                        {errors.userName.length > 0 &&  <label className="block tracking-wide text-red-700
                        text-xs font-medium">{errors.userName}</label>}
                    </div>
                    <div className="fullName w-full md:w-1/2 px-3">
                        <label htmlFor="fullName"
                               className="block uppercase tracking-wide text-gray-700 text-sm font-medium mb-2">
                            Full Name
                        </label>
                        <input
                            className="appearance-none block w-full text-gray-700 border border-gray-400
                            rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                            type="text" placeholder="Full name" name='fullName'
                            onChange={this.handleChange}/>
                        {errors.fullName.length > 0 &&  <label className="block tracking-wide text-red-700
                        text-xs font-medium">{errors.fullName}</label>}
                    </div>
                </div>
                <div className="flex flex-wrap -mx-3 mb-6">
                    <div className="eMail w-full px-3">
                        <label htmlFor="eMail"
                               className="block uppercase tracking-wide text-gray-700 text-sm font-medium mb-2">
                            E-mail
                        </label>
                        <input
                            className="appearance-none block w-full text-gray-700 border border-gray-400
                            rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                            type="email" placeholder="E-mail" name='eMail' onChange={this.handleChange}/>
                        {errors.eMail.length > 0 &&  <label className="block tracking-wide text-red-700
                        text-xs font-medium">{errors.eMail}</label>}
                    </div>
                </div>
                <div className="flex flex-wrap -mx-3 mb-6">
                    <div className="passWord w-full px-3">
                        <label htmlFor="passWord"
                               className="block uppercase tracking-wide text-gray-700 text-sm font-medium mb-2">
                            Password
                        </label>
                        <input
                            className="appearance-none block w-full text-gray-700 border border-gray-400
                            rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                            type="password" placeholder="Password" name='passWord'
                            onChange={this.handleChange} />
                            <div className="absolute inset-y-0 right-0 pr-3 flex items-center text-sm leading-5">
                            <FontAwesomeIcon icon={faEye}/>
                            </div>
                        {errors.passWord.length > 0 &&  <label className="block text-red-700
                        text-xs font-medium">{errors.passWord}</label>}
                        <input
                            className="mt-3 appearance-none block w-full text-gray-700 border border-gray-400
                            rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                            type="password" placeholder="Password confirmation" name='passWord'
                            onChange={this.handleChange}/>
                        {errors.passWord.length > 0 &&  <label className="block tracking-wide text-red-700
                        text-xs font-medium">{errors.passWord}</label>}
                    </div>
                </div>
                <div className="submit">
                    <button className="w-full text-center py-3 rounded bg-gray-700 text-white
                            hover:bg-gray-500 hover:text-gray-900 focus:outline-none my-1">
                        Create Account
                    </button>
                </div>
            </form>
        );
    }
}

export default SignUpForm;
