import React from 'react';
import { useQuery } from 'react-query';
import bk from "./images/bk.png";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faTimes} from "@fortawesome/free-solid-svg-icons";

type Data = { id: number; name: string; phone: string, distance: string };

function Mirage() {

    const { status, data, error } = useQuery<Data[], any>('movies', () =>
        fetch('/api/restaurants').then((x) => x.json())
    );

    return (
        <div>
            <div>
                <div>{status}</div>
                {error && <div>{error}</div>}
                <div>
                    {status === 'loading' ? (
                        <span>Loading...</span>
                    ) : status === 'error' ? (
                        <span>Error: {error!.message}</span>
                    ) : (
                        <div className="w-full max-w-sm mx-auto rounded-md shadow-md overflow-hidden bg-gray-200">
                            {data!.map((movie) => (
                            <div className="flex items-end justify-end h-56 w-full bg-contain bg-center bg-no-repeat"
                                 style={{ backgroundImage:`url(${bk})` }}>
                                key={movie.id}
                        <div className="px-5 py-3 bg-white">

                                <div >
                                    <h3 className="text-gray-700 font-bold"> {movie.name} </h3>
                                    <span className="text-gray-500 mt-2">{movie.phone}</span>
                                    <span className="text-gray-500 mt-2">{movie.distance}</span>
                                </div>

                        </div>
                        </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Mirage;
