import Axios, { AxiosRequestConfig } from 'axios'

export interface Credentials {
  username: string
  fullName: string
  email: string
  password: string
}



export const BOUNCE_IT_TOKEN_KEY = 'bounce_it_token_key'

const storeToken = (token: string): any => {
  localStorage.setItem(BOUNCE_IT_TOKEN_KEY, token)
}

export const onLogin = async (data: Credentials): Promise<any> => {
  const requestConfig: AxiosRequestConfig = {
    method: 'post',
    url: 'http://localhost:4000/auth/login',
    data,
  }

  try {
    const { data: token } = await Axios.request(requestConfig)
    storeToken(token)
    return {
      token,
    }
  } catch (e) {
    console.error(e)
    return { error: e.response.data.message }
  }
}

export const onRegister = async (data: Credentials): Promise<any> => {
  const requestConfig: AxiosRequestConfig = {
    method: 'post',
    url: 'http://localhost:4000/auth/register',
    data,
  }

  try {
    const { data: token } = await Axios.request(requestConfig)
    storeToken(token)
    return {
      token,
    }
  } catch (e) {
    console.error(e)
    return { error: e.response.data.message }
  }
}
