import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck, faTimes } from '@fortawesome/free-solid-svg-icons'

enum ButtonStatus {
    VOTED,
    UNVOTED,
    LOADING
}

const Button: React.FunctionComponent<{ onClick: any, status: ButtonStatus }> = ({ onClick }) => {
  const [button, setButton] = useState(true)

  const toggleButton: () => void = () => {
    setButton(!button)
    onClick()
  }

  return (
    <button
      onClick={toggleButton}
      className={
        button
          ? 'bg-green-600 p-2 rounded-full h-10 w-10 text-white mx-5 -mb-16 hover:bg-green-500 focus:outline-none'
          : 'bg-red-600 p-2 rounded-full h-10 w-10 text-white mx-5 -mb-16 hover:bg-red-500 focus:outline-none'
      }
    >
      <FontAwesomeIcon icon={button ? faCheck : faTimes} />
    </button>
  )
}

export default Button
