import React from 'react';
import { useQuery } from 'react-query';
import bk from "./images/bk.png";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faTimes} from "@fortawesome/free-solid-svg-icons";

type Data = { id: number; name: string; phone: string, distance: string };

function Mirage() {

    const { status, data, error } = useQuery<Data[], any>('movies', () =>
        fetch('/api/restaurants').then((x) => x.json())
    );

    return (
        <div className='px-6 xl:px-12 w-full max-w-3xl mx-auto lg:ml-0 lg:mr-auto xl:mx-0 xl:w-3/4'>
            <div className='px-6 text-left md:text-center xl:text-left max-w-2xl md:max-w-3xl mx-auto'>
                <p className='mt-6 leading-relaxed sm:text-lg md:text-xl xl:text-lg text-gray-600'>
                    Here is a list of movies
                </p>
                <div>{status}</div>
                {error && <div>{error}</div>}
                <div>
                    {status === 'loading' ? (
                        <span>Loading...</span>
                    ) : status === 'error' ? (
                        <span>Error: {error!.message}</span>
                    ) : (
                        <div className="px-5 py-3 bg-white">
                            {data!.map((movie) => (
                                <div key={movie.id}>
                                    <h3 className="text-gray-700 font-bold"> {movie.name} </h3>
                                    <span className="text-gray-500 mt-2">{movie.phone}</span>
                                    <span className="text-gray-500 mt-2">{movie.distance}</span>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
            <div className="w-full max-w-sm mx-auto rounded-md shadow-md overflow-hidden bg-gray-200">
                <div className="flex items-end justify-end h-56 w-full bg-contain bg-center bg-no-repeat"
                     style={{ backgroundImage:`url(${bk})` }}>
                    <button className="p-2 rounded-full h-10 w-10 bg-red-600 text-white mx-5 -mb-16
                        hover:bg-red-500 focus:outline-none focus:bg-blue-500">
                        <FontAwesomeIcon icon={faTimes}/>
                    </button>
                </div>
                <div className="px-5 py-3 bg-white">
                    <h3 className="text-gray-700 font-bold">Burger King</h3>
                    <span className="text-gray-500 mt-2">+36 70 452 3457 <br/></span>
                    <span className="text-gray-500 mt-2">0,85 km</span>
                </div>
            </div>
        </div>
    );
}

export default Mirage;
