import React from 'react';
import "../tailwind.output.css"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCheck} from "@fortawesome/free-solid-svg-icons";
import {useQuery} from "react-query";
import Menu from "./Menu";

type Data = { id: number; name: string; phone: string, distance: string, image : any };

function Card() {

    const { status, data, error } = useQuery<Data[], any>('restaurants', () =>
        fetch('/api/restaurants').then((x) => x.json())
    );

    return (
        <main className="mb-8">
            <div hidden={true}>{status}</div>
            {error && <div>{error}</div>}
            <div>
                {status === 'loading' ? (
                    <span hidden={true}>Loading...</span>
                ) : status === 'error' ? (
                    <span hidden={true}>Error: {error!.message}</span>
                ) : (
                    <div className="container mx-auto px-6">
                        <h3 className="text-gray-700 text-3xl font-bold">Places</h3>
                        <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 mt-6" >
                            {data!.map((restaurant) => (
                                <div className="w-full max-w-sm mx-auto rounded-md shadow-md
                                     overflow-hidden bg-gray-200" key={restaurant.id}>
                                    <div className="flex items-end justify-end h-56 w-full bg-contain
                                         bg-center bg-no-repeat"
                                         style={{ backgroundImage: restaurant.image }}>
                                        <button className="p-2 rounded-full h-10 w-10 bg-green-600
                                                text-white mx-5 -mb-16
                                                hover:bg-green-500 focus:outline-none focus:bg-blue-500">
                                            <FontAwesomeIcon icon={faCheck}/>
                                        </button>
                                    </div>
                                    <div className="px-5 py-3 bg-white">
                                        <h3 className="text-gray-700 font-bold">{restaurant.name}</h3>
                                        <span className="text-gray-500 mt-2">{restaurant.phone}<br/></span>
                                        <span className="text-gray-500 mt-2">{restaurant.distance}</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
            <Menu/>
        </main>
    );
}

export default Card;
