import React, { useEffect, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck, faSearch, faTimes } from '@fortawesome/free-solid-svg-icons'
import Menu from './Menu'
import { RestaurantApi } from '../api/restaurant.api'
import { RestaurantDto } from '../api/dto/restaurant.dto'

const Card: React.FunctionComponent = () => {
  let [restaurants, setRestaurants] = useState<RestaurantDto[]>([])
  useEffect(() => {
    async function fetchAll(): Promise<void> {
      const resp = await RestaurantApi.getAll()

      setRestaurants(resp)
    }
    fetchAll().then(() => {
      console.log('ok')
    })
  }, [])

  const [input, setInput] = useState('')
  const handleChange = (e: any): void => {
    e.preventDefault()
    setInput(e.target.value)
  }

  if (input.length > 0) {
    restaurants = restaurants.filter((i) => {
      return i.name.toLowerCase().match(input) || i.name.toUpperCase().match(input) || i.name.match(input)
    })
  }

  const sortAsc = (): any => {
    const sorted = [...restaurants].sort((a, b) => {
      return a.name.localeCompare(b.name)
    })
    setRestaurants(sorted)
  }

  const sortDesc = (): any => {
    const sorted = [...restaurants].sort((a, b) => {
      return b.name.localeCompare(a.name)
    })
    setRestaurants(sorted)
  }

  const sortDistAsc = (): any => {
    const sorted = [...restaurants].sort((a, b) => {
      return a.distance - b.distance
    })
    setRestaurants(sorted)
  }

  const sortDistDesc = (): any => {
    const sorted = [...restaurants].sort((a, b) => {
      return b.distance - a.distance
    })
    setRestaurants(sorted)
  }

  const [value, setValue] = useState(0 as number)

  const filter = (): any => {
    const sorted = [...restaurants].filter((a) => {
      return a.distance >= value
    })
    setRestaurants(sorted)
  }

  const handleSlide = (e: any): any => {
    setValue(e.target.value)
  }

  return (
    <main className="mb-8">
      <div className="relative mt-6 max-w-md mx-auto">
        <span className="absolute inset-y-0 left-0 pl-3 flex items-center">
          <FontAwesomeIcon className="text-gray-500" icon={faSearch} />
        </span>
        <input
          className="w-full border rounded-md pr-4 py-2
                   focus:border-gray-700 focus:outline-none focus:outline-none text-center"
          type="text"
          placeholder="Search"
          onChange={handleChange}
          value={input}
        />
      </div>
      <div className="mt-6 max-w-md mx-auto flex justify-center">
        <p className="mr-2 rounded text-gray-600 px-2 py-1 w-20 focus:outline-none">Sort by:</p>
        <button
          onClick={sortAsc}
          className="mr-8 rounded border border-gray-400 hover:border-gray-700 text-gray-600 hover:text-gray-900 px-2 py-1 w-16 focus:outline-none"
        >
          A-Z
        </button>
        <button
          onClick={sortDesc}
          className="mr-8 rounded border border-gray-400 hover:border-gray-700 text-gray-600 hover:text-gray-900 px-2 py-1 w-16 focus:outline-none"
        >
          Z-A
        </button>
        <button
          onClick={sortDistAsc}
          className="mr-8 rounded border border-gray-400 hover:border-gray-700 text-gray-600 hover:text-gray-900 px-2 py-1 w-16 focus:outline-none"
        >
          +
        </button>
        <button
          onClick={sortDistDesc}
          className="rounded border border-gray-400 hover:border-gray-700 text-gray-600 hover:text-gray-900 px-2 py-1 w-16 focus:outline-none"
        >
          -
        </button>
        <button
          onClick={filter}
          className="rounded border border-gray-400 hover:border-gray-700 text-gray-600 hover:text-gray-900 px-2 py-1 w-16 focus:outline-none"
        >
          Filter
        </button>
      </div>
      <div className="mt-6 max-w-md mx-auto flex justify-center">
        <input type="range" min="0" max="5" step="1" onChange={handleSlide && filter} />
      </div>
      <div className="container mx-auto px-6 pt-8">
        <h3 className="text-gray-700 text-3xl font-bold cursor-default">Places</h3>
        <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 mt-6">
          {restaurants.map((restaurant) => (
            <div
              className="w-full max-w-sm mx-auto rounded-md shadow-md
                                     overflow-hidden bg-gray-200"
              key={restaurant.id}
            >
              <div
                className="flex items-end justify-end h-56 w-full bg-contain
                                         bg-center bg-no-repeat"
                style={{ backgroundImage: `url(${restaurant.image})` }}
              >
                <button
                  className={
                    restaurant.vote
                      ? 'bg-green-600 p-2 rounded-full h-10 w-10 text-white mx-5 -mb-16 hover:bg-green-500 focus:outline-none'
                      : 'bg-red-600 p-2 rounded-full h-10 w-10 text-white mx-5 -mb-16 hover:bg-red-500 focus:outline-none'
                  }
                >
                  <FontAwesomeIcon icon={restaurant.vote ? faCheck : faTimes} />
                </button>
              </div>
              <div className="px-5 py-3 bg-white">
                <h3 className="text-gray-700 font-bold cursor-default">{restaurant.name}</h3>
                <span className="text-gray-500 mt-2 cursor-default">
                  {restaurant.phone}
                  <br />
                </span>
                <span className="text-gray-500 mt-2 cursor-default">{restaurant.distance} km</span>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Menu />
    </main>
  )
}

export default Card
